	--step--1: clamp(0.8021rem, 0.768rem + 0.1703vw, 0.9rem);				/* 14.40 p */
	--step-0: clamp(0.875rem, 0.8315rem + 0.2174vw, 1rem);					/* 16.00 p */
	--step-1: clamp(0.9625rem, 0.906rem + 0.2826vw, 1.125rem);			/* 18.00 p */
	--step-2: clamp(1.05rem, 0.9804rem + 0.3478vw, 1.25rem);				/* 20.00 p */
	--step-3: clamp(1.26rem, 1.1548rem + 0.5261vw, 1.5625rem);			/* 25.00 p */
	--step-4: clamp(1.386rem, 1.2567rem + 0.6466vw, 1.7578rem);			/* 28.12 p */
	--step-5: clamp(1.512rem, 1.3586rem + 0.7672vw, 1.9531rem);			/* 31.25 p */
	--step-6: clamp(1.8144rem, 1.5963rem + 1.0904vw, 2.4414rem);		/* 39.06 p */
	--step-6_5: clamp(1.9959rem, 1.7347rem + 1.3056vw, 2.7466rem);	/* 43.94 p */
	--step-7: clamp(2.1773rem, 1.8731rem + 1.5208vw, 3.0518rem);		/* 48.83 p */
	--step-8: clamp(2.6127rem, 2.1947rem + 2.0904vw, 3.8147rem);		/* 61.04 p */
	--step-9: clamp(3.1353rem, 2.5673rem + 2.8402vw, 4.7684rem);		/* 76.29 p */
	--step-10: clamp(3.7623rem, 2.9978rem + 3.8228vw, 5.9605rem);		/* 95.37 p */
}

/* reset */
/* ========================================================================== */

html {
	box-sizing: border-box;
}

*, *:before, *:after {
	box-sizing: inherit;
}

img, video {
	max-width: 100%;
	vertical-align: middle;
}

h1, h2, h3, h4, h5, h6 {margin-block: 0;font-weight: 850;text-wrap: balance}

a {
	color: inherit;
}
.material-symbols-outlined.filled {
  font-variation-settings:
  'FILL' 1,
  'wght' 400,
  'GRAD' 0,
  'opsz' 24
}
.material-symbols-outlined {
  font-variation-settings:
  'FILL' 0,
  'wght' 400,
  'GRAD' 0,
  'opsz' 24
}

/* general */
/* ========================================================================== */
body {
	font-family: "Mulish", sans-serif;
	font-weight: 400;
	line-height: 1.4;
	scroll-behavior: smooth;
	/* overflow-x: hidden; */
	color: #000;
	background-color: var(--bg-clr-one);
	font-size: var(--step-0, 1rem);
}
.main {
	margin: 0 auto;
	padding-top: var(--header-height);
}
.section {
	padding-block: calc(var(--padding) * 2.5);
	padding-inline: var(--padding);
}
.container {
	margin-inline: auto;
	max-width: 75rem;
}
.container-wide {
	margin-inline: auto;
	max-width: 120rem;
}

/* @media (max-width: 95.9375rem) {
	.container {
		max-width: 81.25rem;
	}
}
@media (max-width: 85.375rem) {
	.container {
		max-width: 75rem;
	}
} */

@media (max-width: 47.9375rem) {
	.hidden-mobile {display: none!important;}
}
@media (min-width: 48rem) and (max-width: 63.9375rem) {
	.hidden-tablet {display: none!important;}
}
@media (min-width: 64rem) {
	.hidden-desktop {display: none!important;}
	.section {
		padding-inline: calc(var(--padding) * 2.5);
	}
}

@media (max-width: 35.9375rem) {
	.section {
		padding-block: calc(var(--padding) * 1.75);
	}
}

@media (max-width: 47.9375rem) {
	body {
		font-size: var(--step-1);
	}
}

/* hr*/
/* ========================================================================== */
hr {
	margin-block: var(--padding);
	border: 0;
	border-top: .0938rem solid var(--clr-border);
}

/* other */
/* ========================================================================== */
.accent {
	color: var(--clr-one);
}

/* buttons */
/* ========================================================================== */
.btn {
	display: inline-flex;
	gap: 0.5em;
	color: var(--clr-one);
	background-color: #fff;
	border: .15rem solid var(--clr-one);
	/* box-shadow: 0 0 0 .15rem var(--clr-one); */
	padding: 0.65em 1.65em;
	border-radius: var(--radius);

	font-family: inherit;
	font-weight: 700;
	font-size: var(--step-1);
	text-decoration: none;
	text-align: center;
	cursor: pointer;

	align-items: center;
	justify-content: center;
	justify-items: center;
	align-content: center;

	transition: background-color var(--transition-function) var(--transition-time, 0.25s),
							color var(--transition-function) var(--transition-time, 0.25s),
							fill var(--transition-function) var(--transition-time, 0.25s),
							box-shadow var(--transition-function) var(--transition-time, 0.25s);
}
.btn--bg {
	color: #fff;
	background-color: var(--clr-one);
}
.btn--black {
	color: #fff;
	background-color: #121212;
	box-shadow: none;
}
.btn img,
.btn svg {
	width: 1em;
	height: 1em;
	object-fit: contain;
	vertical-align: middle;
	color: currentColor;
	fill: currentColor;
}
.btn:hover,
.btn.active {
	color: var(--clr-one);
	background-color: #fff;
}
.btn--black:hover {
	background-color: #000;
}

.btn-arw {
	position: relative;
	/* padding: 12px 30px; */
	padding: 0.8em 1.65em;
	background-color: var(--clr-one);
	color: white;
	border: none;
	border-radius: var(--radius);
	font-family: inherit;
	font-weight: 700;
	font-size: var(--step-1);
	text-decoration: none;
	text-align: center;

	cursor: pointer;
	overflow: hidden;
	transition: padding 0.3s ease;
}
.btn-arw span {
	display: inline-block;
}
.btn-arw::after {
	content: '';
	position: absolute;
	right: -2rem;
	top: 50%;
	transform: translateY(-50%);
	width: 2rem;
	height: .65rem;
	background-color: white;
	clip-path: polygon(0% 32%, 70% 32%, 70% 0%, 100% 50%, 70% 100%, 70% 68%, 0% 68%);
	opacity: 0;
	transition: right 0.3s ease, opacity 0.3s ease;
}

@media (hover: hover) {
	.btn-arw:hover {
		padding-right: 55px;
	}
	.btn-arw:hover::after {
		right: 15px;
		opacity: 1;
	}
}

/* header */
/* ========================================================================== */
.header {
	position: fixed;
	z-index: 15;
	width: 100%;
}
.header__inner {
	background: #fff;
	box-shadow: 0 .25rem 1.25rem #0000000a;
	border-bottom: .0625rem solid #eee;
	padding-inline: var(--padding);
	padding-block: 1rem;
}
.header__content {
	display: grid;
	grid-template-columns: repeat(3, auto);
	grid-template-rows: auto;
	column-gap: 1rem;
	justify-content: space-between;
	align-items: center;
}

.header__menu {
	grid-column: 1 / -1;
}
.hamburger {
}
.header__logo {
	grid-column: 2 / 3;
	grid-row: 1 / 1;
}
.header__cart {
	grid-column: 3 / 4;
	grid-row: 1 / 1;
}

.header__logo {
	line-height: 1;
}
.header__logo-img {
	width: 8.125rem;
}
.header__menu {
	padding-left: 0;
	list-style: none;
	margin: 0;
	max-height: 0;
	transition: max-height var(--transition-time, 0.25s) ease-in-out;
	overflow: hidden;
}
.header__menu-item {}
.header__menu-link {
	display: inline-block;
	padding-block: 0.45em;
	text-decoration: none;
	text-transform: uppercase;
	line-height: 1;
}
.header__hamburger-btn {
	display: none;
}
.header__hamburger-icon {
	padding-block: .9375rem;
	position: relative;
	cursor: pointer;
}
.header__icon {
	width: 1.375rem;
	height: .125rem;
	display: block;
	background: #333;
	position: relative;
	border-radius: .3125rem;
	transition: background var(--transition-time, 0.25s) ease-in-out;
}
.header__icon::before,
.header__icon::after {
	content: '';
	width: 100%;
	height: 100%;
	display: block;
	background: #333;
	position: absolute;
	border-radius: .3125rem;
	transition: all var(--transition-time, 0.25s) ease-in-out;
}
.header__icon::before {
	top: -0.4375rem;
}
.header__icon::after {
	top: .4375rem;
}
.header__hamburger-btn:checked~.header__menu {
	max-height: 15rem;
}
.header__hamburger-btn:checked~.header__hamburger-icon .header__icon {
	background: transparent;
}
.header__hamburger-btn:checked~.header__hamburger-icon .header__icon::before {
	transform: rotate(45deg);
	top: 0;
}
.header__hamburger-btn:checked~.header__hamburger-icon .header__icon::after {
	transform: rotate(-45deg);
	top: 0;
}

@media (max-width: 47.9375rem) {
	.header__menu-item:first-child {
		margin-top: 1rem;
	}
}

@media (min-width: 64rem) {
	.header__menu {
		display: flex;
		gap: 0 1rem;
		max-height: none;
		width: 445px;
		flex-wrap: wrap;
		grid-row: 1;
	}
	.header__content {
		justify-content: initial;
	}
	.header__cart {
		justify-self: end;
	}
	.header__hamburger-icon {
		display: none;
	}
}

/* --- */
.header {transition: transform 0.3s ease-in-out;}
.header.hidden {transform: translateY(-100%);}

/* horizontal ticker */
/* ========================================================================== */
.horizontal-ticker {
	overflow-x: hidden;
	max-width: 100%;
	color: #fff;
	background-color: #000;
	padding-block: 1rem;
	font-weight: 800;
}
.horizontal-ticker__container {
	display: flex;
	align-items: center;
	width: fit-content;
	gap: 50px;
}
.horizontal-ticker__item {
	margin: 0;
	line-height: 1;
	white-space: nowrap
}
/* START horizontal-ticker animation */
.horizontal-ticker__container {animation: mobileHorTickersections 75s linear infinite normal forwards;}
.horizontal-ticker-sections:hover .horizontal-ticker__container {animation-play-state: paused}
@media screen and (min-width: 750px) {.horizontal-ticker__container {animation-name: desktopHorTickersections}}
@keyframes mobileHorTickersections {to {transform: translateX(calc(-50% - 15.0px))}}
@keyframes desktopHorTickersections {to {transform: translateX(calc(-50% - 25.0px))}}
/* END horizontal-ticker animation */

@media (max-width: 63.9375rem) {
	.horizontal-ticker {
		font-weight: 700;
		padding-block: 0.75rem;
	}
}

.header .horizontal-ticker {
	transition: all 0.25s ease-out;
}
.ticker-hidden {
	transform: translateY(-100%);
	opacity: 0;
	visibility: hidden;
	max-height: 0;
	padding-top: 0;
	padding-bottom: 0;
	margin-top: 0;
	margin-bottom: 0;
}

/* product rating */
/* ========================================================================== */
.product-rating {
	display: inline-flex;
	flex-wrap: wrap;
	align-items: center;
	flex-grow: 0;
	gap: var(--gap-xxs, .625rem);
