/* section hero */
/* ========================================================================== */
.sct-hero {}
.sct-hero__content {
	display: grid;
	grid-template-columns: repeat(2, minmax(12.5rem, 100%));
	gap: var(--gap-xxl);
	position: relative;
	/* align-items: center; */
}
.sct-hero__content-row.--sticky {
