/* START section hero - gallery slider */
/* ========================================================================== */

/* Swiper slider - base styles */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.swiper {
	width: 100%;
	height: 100%;
	border-radius: var(--slider-radius);
	box-sizing: border-box;
}
.swiper-slide {
	overflow: hidden;
	border-radius: var(--slider-radius);
}
.swiper-slide img {
	max-width: 100%;
	object-fit: contain;
	vertical-align: middle;
}

/* Main Slider Styles */
/* ------------------------- */
.gallery-slider {
	width: 100%;
	margin-bottom: .625rem; /* Space between main slider and thumbs */
}
.gallery-slider .swiper-slide {
	display: flex;
	justify-content: center;
	align-items: center;
	background-color: #eee;
}

/* Thumbs Slider Styles */
/* ------------------------- */
.gallery-thumbs {}
.gallery-thumbs .swiper-slide {
	box-sizing: border-box; /* Include padding/border */
	display: flex;
	justify-content: center;
	align-items: center;
	cursor: pointer;
	font-size: .75rem;
	background-color: #eee;
	/* opacity: 0.4; */
	text-align: center;
}
.gallery-thumbs .swiper-slide-thumb-active {
	opacity: 1;
	border: .125rem solid var(--slider-color); /* Highlight active thumb */
}

/* Swiper slider - advanced styles and effects */
/* ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~ */
.gallery-container {
	--slider-color: var(--clr-one, #0133aa);
	/* --slider-radius: 0.5rem; */
	--swiper-navigation-color: var(--slider-color);
	--swiper-pagination-color: var(--slider-color);
	--swiper-navigation-sides-offset: 1rem;
	--swiper-thumbnails-size: 4.0625rem;
}
.gallery-container .swiper-button-next, .gallery-container .swiper-button-prev {
	--swiper-nav-button-size: clamp(1.875rem, 1.6023rem + 1.3636vw, 2.8125rem);
	top: calc(50% + (var(--swiper-nav-button-size) / 4));
	aspect-ratio: 1 / 1;
	width: var(--swiper-nav-button-size);
	height: var(--swiper-nav-button-size);
	background: var(--slider-color);
	color: #fff;
	border-radius: 3em;
}
.gallery-container .swiper-button-next:after, .gallery-container .swiper-button-prev:after{
	font-size: 0.625rem;
	line-height: 1;
	text-align: center;
}
.gallery-container .swiper .swiper-button-prev.swiper-button-disabled,
.gallery-container .swiper .swiper-button-next.swiper-button-disabled {
	opacity: 0.75;
}

/* animation for nav buttons */
/* ------------------------- */
.gallery-container .swiper .swiper-button-next, .gallery-container .swiper .swiper-button-prev {
	opacity: 0;
	transition: opacity ease-in-out var(--transition-time, 0.25s),
							box-shadow ease-in-out var(--transition-time, 0.25s),
							transform ease-in-out var(--transition-time, 0.25s);
}
.gallery-container .swiper .swiper-button-prev {transform: translate3d(-200%, 0, 0)}
.gallery-container .swiper .swiper-button-next {transform: translate3d(200%, 0, 0)}
.gallery-container .swiper:hover .swiper-button-next, .gallery-container .swiper:hover .swiper-button-prev {
	opacity: 1;
	transform: none;
}
.gallery-container .swiper .swiper-button-prev:hover, .gallery-container .swiper .swiper-button-next:hover {
	box-shadow: 0 0 0 .2rem var(--slider-color);
}


@media (min-width: 767px) {
	.gallery-slider .swiper-wrapper {
		display: grid;
		grid-template-columns: repeat(2, 1fr);
		gap: var(--step--3);
	}
	.gallery-slider .swiper-slide:first-child {
		grid-column: 1 / -1;
	}
	.gallery-thumbs {
		display: none;
	}
}

@media (max-width: 767px) {
	.gallery-slider {
		margin-inline: calc(var(--padding) * -1);
		width: calc(100% + (var(--padding) * 2));
	}
}
/* ========================================================================== */
/* END section hero - gallery slider */



/* micromodal */
/* ========================================================================== */
.micromodal {
	display: none;
	--padding: var(--step-1);
}
.micromodal.is-open {
	display: block;
}
.micromodal__overlay {
	position: fixed;
	top: 0;
	left: 0;
	right: 0;
	bottom: 0;
	display: flex;
	justify-content: center;
	align-items: center;
	background: rgba(0,0,0,0.65);
	z-index: 3;
}
.micromodal__container {
	box-sizing: border-box;
	overflow-y: auto;
	max-width: 500px;
	max-height: 100vh;
	padding: var(--padding);
	background-color: #fff;
	border-radius: var(--radius-big);
	position: relative;
}
.micromodal__btn-close {
	display: block;
	position: absolute;
	right: 0;
	margin-right: var(--padding);
	background: transparent;
	border: 0;
	padding: 0;
	line-height: 1;
	/* background-color: red; */
	cursor: pointer;
}
.micromodal__btn-close svg {
	height: 1.15rem;
	width: 1.15rem;
}

.micromodal[aria-hidden="false"] .micromodal__overlay {
	animation: microModalFadeIn .2s cubic-bezier(0.0, 0.0, 0.2, 1);
}
.micromodal[aria-hidden="false"] .micromodal__container {
	animation: microModalSlideIn .2s cubic-bezier(0, 0, .2, 1);
}
.micromodal .micromodal__container,
.micromodal .micromodal__overlay {
	will-change: transform;
}
@keyframes microModalFadeIn {
		from { opacity: 0; }
			to { opacity: 1; }
}
@keyframes microModalSlideIn {
	from { transform: translateY(15%); }
		to { transform: translateY(0); }
}

/* modal-size */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -  */
.modal-size {
	margin-bottom: 1rem;
}
.modal-size__title {
	margin-bottom: 0.75rem;
}
.modal-size__btn {
	display: flex;
	align-items: center;
	text-decoration: none;
	gap: 0.5ch;
	font-size: var(--step--1);
}
.modal-size__btn-text {
	text-decoration: underline;
}

.modal-size .size-table tr:first-child td,
.modal-size .size-table tr:first-child:hover td {
	color: #fff!important;
	background-color: #000!important;
	border-color: #555555!important;
}
.modal-size .size-table th,
.modal-size .size-table td:first-child {
	color: #292938!important;
	background-color: transparent!important;
