/* link (non-underlined) animation */
/* ========================================================================== */

@media (min-width: 48rem) {
	.footer__info-links a,
	.link--nu {
		display: inline-flex;
		align-items: center;
		justify-self: flex-start;
		position: relative;
	}

	@media (hover: hover) {
		.footer__info-links a:hover:after,
		.link--nu:hover:after {
			width: 100%;
			text-decoration: none;
		}
	}
			.footer__info-links a.active:after,
		.link--nu.active:after {
			width: 100%;
			text-decoration: none;
		}

	.footer__info-links a:after,
	.link--nu:after {
		position: absolute;
		content: "";
		width: 0;
		height: 0.1em;
		background-color: currentColor;
		left: 0;
		bottom: 0.1em;
		transition: width var(--transition-time, 0.25s) var(--transition-function);
	}
}

/* link (underlined) + animation */
/* ========================================================================== */
.link {
	position: relative;
	color: currentColor;
	font-weight: bold;
	display: inline-flex;
	justify-self: flex-start;
	padding-bottom: 0.1em;
	overflow: hidden;
	text-underline-offset: 0.35em;
}

/* animation effects only for desktop */
@media (min-width: 48rem) {
	.link {
		text-decoration: none;
	}

	/* Underline in its original state */
	.link::after,
	.link::before {
		content: '';
		position: absolute;
		width: 100%;
		height: 0.1em;
		bottom: 0;
		left: 0;
		background-color: currentColor;
	}

	/* Animation layer - initially hidden with zero opacity */
	.link::before {
		opacity: 0;
	}

	/* We activate the animation layer on interaction */
	.link:hover::before {
		opacity: 1;
		animation: underlineMove 0.75s ease forwards;
	}

	/* To animate the stroke, add a class via JavaScript */
	.link.animate-out::before {
		opacity: 1;
		animation: underlineMoveReverse 0.75s ease forwards;
	}

	/* Hide the static underline on hover */ 
	.link:hover::after, .link.animate-out::after {
		opacity: 0;
	}

	/* Hover animation */
	@keyframes underlineMove {
		0% {transform: translateX(0);}
		49% {transform: translateX(100%);}
		50% {transform: translateX(-100%);}
		100% {transform: translateX(0);}
	}

	/* Hover animation */
	@keyframes underlineMoveReverse {
		0% {transform: translateX(0);}
		49% {transform: translateX(-100%);}
		50% {transform: translateX(100%);}
		100% {transform: translateX(0);}
	}
}

