            try {
                let referencePrice = variationsArr[0].reducedPrice;
                for(let v = 0; v < variationsArr.length; v++){
                    if(variationsArr[v].reducedPrice !== referencePrice){
                        return false;
                    }
                }//for
            }catch(err){
                return false;
            }
            return true;
        }//allVariationPricesAreEqual
