	<script>/* bottomStickyBar — sticky bar that appears after 20% of the scroll */
/* ========================================================================== */
window.addEventListener('scroll', function () {
	const stickyBar = document.getElementById('bottomStickyBar');
	if (!stickyBar) return;

	const scrollTop = window.scrollY;
	const docHeight = document.documentElement.scrollHeight - window.innerHeight;
	const scrolledPercent = scrollTop / docHeight;

	if (scrolledPercent > 0.2) {
		stickyBar.classList.add('visible');
	} else {
		stickyBar.classList.remove('visible');
	}
});


/* link (underlined) animation */
/* ========================================================================== */

document.addEventListener('DOMContentLoaded', function () {
	const links = document.querySelectorAll('.link');

	links.forEach(link => {
		// Variable to track state (whether it was hovered)
		let wasHovered = false;

		// On hover, set flag
		link.addEventListener('mouseenter', function () {
			wasHovered = true;
			link.classList.remove('animate-out');
		});

		// On mouse leave, check flag
		link.addEventListener('mouseleave', function () {
			if (wasHovered) {
				link.classList.add('animate-out');

				// Remove class after animation ends
				setTimeout(() => {
					link.classList.remove('animate-out');
				}, 800); // Time should match animation duration
			}
		});
	});
});

/* .header height & .bottom-sticky-bar height */
/* ========================================================================== */
document.addEventListener("DOMContentLoaded", function () {
	function updateHeights() {
		const header = document.querySelector(".header");
		const stickyBar = document.querySelector(".bottom-sticky-bar");

		if (header) {
			const headerHeight = header.offsetHeight;
			document.documentElement.style.setProperty("--header-height", `${headerHeight}px`);
		}

		if (stickyBar) {
			const stickyBarHeight = stickyBar.offsetHeight;
			document.documentElement.style.setProperty("--sticky-bar-height", `${stickyBarHeight}px`);
		}
	}
	
	updateHeights();
	
	window.addEventListener("resize", updateHeights);
	
	const observer = new ResizeObserver(updateHeights);
	if (document.querySelector(".header")) {
		observer.observe(document.querySelector(".header"));
	}
	if (document.querySelector(".bottom-sticky-bar")) {
		observer.observe(document.querySelector(".bottom-sticky-bar"));
	}
});



// ------------
let lastScrollTop = 0;
let scrollStart = 0;
const scrollThreshold = 50; // 50px
const header = document.querySelector('.header');

function getHeaderHeight() {
	const headerStyles = getComputedStyle(document.documentElement);
	const headerHeightValue = headerStyles.getPropertyValue('--header-height');
	return parseInt(headerHeightValue) || 80;
}

const headerHeight = getHeaderHeight();

window.addEventListener('scroll', function () {
	let scrollTop = window.pageYOffset || document.documentElement.scrollTop;
	if (scrollTop <= headerHeight) {
		header.classList.remove('hidden');
		lastScrollTop = scrollTop;
		return;
	}
	if (scrollTop > lastScrollTop) {
		if (scrollStart === 0) {
			scrollStart = scrollTop;
		}
		if (scrollTop - scrollStart > scrollThreshold) {
			header.classList.add('hidden');
		}
	} else if (scrollTop < lastScrollTop) {
		header.classList.remove('hidden');
		scrollStart = 0;
	}
	lastScrollTop = scrollTop;
});


// /* --- */
// .header {transition: transform 0.3s ease-in-out;}
// .header.hidden {transform: translateY(-100%);}

// ------------
const ticker = document.querySelector('.header .horizontal-ticker');

if (ticker) {
	const checkScroll = () => {
		if (window.scrollY > 120) {
			ticker.classList.add('ticker-hidden');
		} else {
			ticker.classList.remove('ticker-hidden');
		}
	};

	window.addEventListener('scroll', checkScroll);
	checkScroll();
}


// ------------
document.addEventListener('DOMContentLoaded', () => {
  const scrollTriggers = document.querySelectorAll('[data-scroll]');
  scrollTriggers.forEach(trigger => {
    trigger.addEventListener('click', function (event) {
      event.preventDefault();
      const targetSelector = this.dataset.scroll;
      if (!targetSelector) return;
      const targetElement = document.querySelector(targetSelector);
      if (targetElement) {
        const offset = parseInt(this.dataset.scrollOffset, 10) || 0;
        const elementPosition = targetElement.getBoundingClientRect().top;
        const offsetPosition = elementPosition + window.pageYOffset - offset;
        window.scrollTo({
          top: offsetPosition,
          behavior: 'smooth'
        });
      } else {
        console.warn(`Scrollable element with selector "${targetSelector}" not found.`);
      }
    });
  });
});</script>

