<script>
	var details = document.querySelectorAll("details.accordion__item");
	for (var i = 0; i < details.length; i++) {
		details[i].addEventListener("toggle", accordion);
	}
	function accordion(event) {
		if (!event.target.open) return;
		var details = document.querySelectorAll("details.accordion__item");
		for (var i = 0; i < details.length; i++) {
			if (details[i] !== event.target && details[i].hasAttribute('open')) {
				details[i].removeAttribute("open");
			}
		}
	}
</script>

<style>
  .buyer-notification .wrapper {
  width:auto !important;
  position: relative; /* */
  padding: 1rem 3rem 1rem 1.5rem;
}
.buyer-notification .data {
  flex-direction:row;
}

.buyer-notification .rows,
.buyer-notification .split {
  align-items: center; /* */
}

.buyer-notification .rows {
  flex-direction:row;
}        
.buyer-notification {
  z-index:999999999 !important;
  top:0;
  margin-top:3.3rem;
  width:auto !important;
  text-align:center;
  height: 40px;
}
.buyer-notification div.img-wrapper {
  width:20px;
  height:20px;
}
.buyer-notification div.img-wrapper div.img {
  width:20px;
  height:20px;
}
.buyer-notification div.img-wrapper div.img img {
  vertical-align: middle; /* */
}
.buyer-notification .close {
  right: 1.25rem; /* */
  top: 50%; /* */
  transform: translateY(-50%); /* */
}

@-webkit-keyframes slide-in-bottom {
  0% {
    -webkit-transform: translateX(1000px);
    transform: translateX(1000px);
    opacity: 0
  }
  100% {
    -webkit-transform: translateX(0);
    transform: translateX(0);
    opacity: 1
  }
}
@keyframes slide-in-bottom {
  0% {
    -webkit-transform: translateX(1000px);
    transform: translateX(1000px);
    opacity: 0
  }
  100% {
    -webkit-transform: translateX(0);
    transform: translateX(0);
    opacity: 1
  } 
}
@keyframes slide-far-out-bottom {
  0% {
    transform: translateX(1000px)
  }
  100% {
    transform: translateX(10000px)
  }
}

@media screen and (max-width: 768px) {
  .buyer-notification {
    right: clamp(.2rem, 4.2vw, 2rem);
  }
  .buyer-notification .wrapper {
    padding: .6rem 1.5rem .6rem .8rem; /* */
  }
  .buyer-notification .close {
    right: .5rem; /* */
  }
  .buyer-notification .split,
  .buyer-notification .message,
  .buyer-notification .product {
    font-size: .8rem; /* */
    min-width: max-content; /* */
  }
  .buyer-notification div.img-wrapper {
    margin-right: .5rem; /* */
  }
  
  .inline-title, .title {
    font-size:1.2rem
  }
  section.intro .intro {
    font-size:1.2rem
  }
}

@media screen and (max-width: 420px) {
  .buyer-notification .split,
  .buyer-notification .message,
  .buyer-notification .product {
    font-size: clamp(.68rem, 3vw, .8rem); /* */
  }
}

/* .buyer-notification .data .row{
       margin-right: 3px;
   }
*/
</style>

