<style>
.review {
	overflow: hidden;
}

.minimized {
  cursor: pointer;
  transition: all .5s;
}

.minimized:hover {
  transform: scale(1.1) translateY(-2%);
}

#magnify {
  display: none;
  position: fixed;
  max-width: 600px;
  height: auto;
  z-index: 9999;
}

#magnify img {
  width: 100%;
}

#overlay {
  display: none;

  background: #000;
  position: fixed;
  top: 0;
  left: 0;
  height: 100%;
  width: 100%;
  opacity: 0.5;
  z-index: 9990;
}

#close-popup {
  width: 24px;
  height: 24px;
  background: #ffffff7a;
  border: 1px solid #0000003b;
  border-radius: 15px;
  cursor: pointer;
  position: absolute;
  top: 15px;
  right: 15px;
}

#close-popup i {
  width: 24px;
  height: 24px;
  background: url(data:image/svg+xml;base64,) no-repeat center center;
  background-size: 10px 10px;
  background-position: 45% 45%;
  display: block;
  opacity: .5;
}

@keyframes rota {
 25% { transform: rotate(360deg); }
}

#close-popup:hover {
  animation: rota 4s infinite normal;
  -webkit-animation-iteration-count: 1;
  animation-iteration-count: 1;
}
</style>

