    //HANDLE ADDITIONAL VARIATIONS PRICES
    function handleAdditionalVariations(price, countryCurrencySymbol, additionalVariationsPrices, additionalVariationProductPrice,country){
        const fvar = extractFvar(window.location.href);
        const defaultPriceOffsetByCurrency = {
            'BG': 10,
            'CZ': 125,
            'HU': 2000,
            'PL': 25,
            'RO': 25,
            'RS': 600,
            'default': 5
        };
        
        const priceOffset = defaultPriceOffsetByCurrency[country] || defaultPriceOffsetByCurrency['default']
        let calculatedPrice = 0;

        //Check if Fvar exists in URL and set position of Additional Variation            
        function extractFvar(url){
            let fvar = "";

            const fvarParam = new URLSearchParams(window.location.search);
            if (fvarParam.has('fvar')){
                fvar = fvarParam.get('fvar').toLowerCase();
            } else{
                return;
            }
            return fvar
        }

        let mainAdditionalVariationIndex = additionalVariationsPrices.findIndex(mvi => !!mvi.src && mvi.src.toLowerCase().includes(fvar));
        if(mainAdditionalVariationIndex > -1){
            let mainAddVarObj = additionalVariationsPrices.splice(mainAdditionalVariationIndex, 1)[0]; //remove object from array
            //adds element to the first position
            additionalVariationsPrices.unshift(mainAddVarObj);
            }
        console.log(additionalVariationsPrices)
        
        if (!!additionalVariationsPrices && additionalVariationsPrices !== "0" && additionalVariationsPrices !== 0 && additionalVariationsPrices !== "" && additionalVariationsPrices.length > 0){
            let validAddVarIndex = 0;
            document.querySelectorAll('.hs-fv-all').forEach((element, index) =>{
                if(index > 0){
                try {
                    let matchingAddVar = additionalVariationsPrices[validAddVarIndex];
                    let imageUrlElement = element.querySelector('.hs-fv-imageurl img');
                    let priceElement = element.querySelector('.hs-fv-price');
                    let finalPrice = matchingAddVar.price;
                    let matchingImageUrl = matchingAddVar.src;

                        //Set matching Price
                        if (!!finalPrice && finalPrice !=0){
                            priceElement.innerHTML = finalPrice + countryCurrencySymbol;
                        } else if (!!additionalVariationProductPrice &&  additionalVariationProductPrice !==0){
                            priceElement.innerHTML = price + additionalVariationProductPrice + countryCurrencySymbol;
                        } else {
                            priceElement.innerHTML = ((price + priceOffset).toFixed(2)) + countryCurrencySymbol; 
                        }

                    //Set matching image
                    if (!!matchingImageUrl) {
                        imageUrlElement.src = matchingImageUrl;
                    }
                    validAddVarIndex++;
                }catch(err){
                    // throw err;
                    }
                }
            })
        } 
        
        //SWIPPER SLIDER FOR F VARIATIONS
            var swiper = new Swiper(".mySwiper", {
            slidesPerView: 'auto',
            spaceBetween: 5,
            loop:false,
            pagination: {
                el: '.swiper-pagination',
                clickable: true
            },
        });
    }
